#[cfg(feature = "devnet2")]
use alloy_primitives::B256;
use alloy_primitives::FixedBytes;
#[cfg(feature = "devnet2")]
use ream_post_quantum_crypto::lean_multisig::aggregate::AggregateSignature;
use ream_post_quantum_crypto::leansig::signature::Signature;
use serde::{Deserialize, Serialize};
use ssz_derive::{Decode, Encode};
use ssz_types::{BitList, VariableList, typenum::U4096};
#[cfg(feature = "devnet2")]
use tree_hash::TreeHash;
use tree_hash_derive::TreeHash;

use crate::checkpoint::Checkpoint;

/// Key for signature storage, combining validator ID and attestation data root.
/// Used for both gossip_signatures and aggregated_payloads maps.
#[cfg(feature = "devnet2")]
#[derive(
    Debug, PartialEq, Eq, Clone, Hash, Serialize, Deserialize, Encode, Decode, PartialOrd, Ord,
)]
pub struct SignatureKey {
    pub validator_id: u64,
    pub data_root: B256,
}

#[cfg(feature = "devnet2")]
impl SignatureKey {
    pub fn new(validator_id: u64, attestation_data: &AttestationData) -> Self {
        Self {
            validator_id,
            data_root: attestation_data.tree_hash_root(),
        }
    }

    pub fn from_parts(validator_id: u64, data_root: B256) -> Self {
        Self {
            validator_id,
            data_root,
        }
    }
}

#[cfg(feature = "devnet2")]
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode)]
pub struct AggregatedSignatureProof {
    pub participants: BitList<U4096>,
    pub proof: AggregateSignature,
}

#[cfg(feature = "devnet2")]
impl AggregatedSignatureProof {
    pub fn new(participants: BitList<U4096>, proof: AggregateSignature) -> Self {
        Self {
            participants,
            proof,
        }
    }

    /// Get the validator IDs covered by this proof
    pub fn to_validator_indices(&self) -> Vec<u64> {
        self.participants
            .iter()
            .enumerate()
            .filter(|(_, bit)| *bit)
            .map(|(index, _)| index as u64)
            .collect()
    }
}

/// Attestation content describing the validator's observed chain view.
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash, Hash)]
pub struct AttestationData {
    pub slot: u64,
    pub head: Checkpoint,
    pub target: Checkpoint,
    pub source: Checkpoint,
}

/// Validator specific attestation wrapping shared attestation data.
#[cfg(feature = "devnet1")]
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash)]
pub struct Attestation {
    pub validator_id: u64,
    pub data: AttestationData,
}

#[cfg(feature = "devnet1")]
impl Attestation {
    /// Return the attested slot.
    pub fn slot(&self) -> u64 {
        self.data.slot
    }

    /// Return the attested head checkpoint.
    pub fn head(&self) -> Checkpoint {
        self.data.head
    }

    /// Return the attested target checkpoint.
    pub fn target(&self) -> Checkpoint {
        self.data.target
    }

    /// Return the attested source checkpoint.
    pub fn source(&self) -> Checkpoint {
        self.data.source
    }
}

#[cfg(feature = "devnet2")]
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash)]
pub struct AggregatedAttestations {
    pub validator_id: u64,
    pub data: AttestationData,
}

#[cfg(feature = "devnet2")]
impl AggregatedAttestation {
    /// Return the attested slot.
    pub fn slot(&self) -> u64 {
        self.message.slot
    }

    /// Return the attested head checkpoint.
    pub fn head(&self) -> Checkpoint {
        self.message.head
    }

    /// Return the attested target checkpoint.
    pub fn target(&self) -> Checkpoint {
        self.message.target
    }

    /// Return the attested source checkpoint.
    pub fn source(&self) -> Checkpoint {
        self.message.source
    }
}

/// Validator attestation bundled with its signature.
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash)]
pub struct SignedAttestation {
    #[cfg(feature = "devnet2")]
    pub validator_id: u64,
    #[cfg(feature = "devnet2")]
    pub message: AttestationData,
    #[cfg(feature = "devnet1")]
    pub message: Attestation,
    /// signature over attestaion message only as it would be aggregated later in attestation
    pub signature: Signature,
}

/// Aggregated attestation consisting of participation bits and message.
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash)]
pub struct AggregatedAttestation {
    /// U4096 = VALIDATOR_REGISTRY_LIMIT
    pub aggregation_bits: BitList<U4096>,
    pub message: AttestationData,
}

/// Aggregated attestation bundled with aggregated signatures.
#[derive(Debug, PartialEq, Eq, Clone, Serialize, Deserialize, Encode, Decode, TreeHash)]
pub struct SignedAggregatedAttestation {
    pub message: AggregatedAttestation,
    /// U4096 = VALIDATOR_REGISTRY_LIMIT
    pub signature: VariableList<FixedBytes<4000>, U4096>,
}

#[cfg(test)]
#[cfg(feature = "devnet1")]
mod tests {
    use alloy_primitives::hex;
    use ssz::{Decode, Encode};

    use super::*;
    use crate::{attestation::AttestationData, checkpoint::Checkpoint};

    #[test]
    fn test_encode_decode_signed_attestation_roundtrip() -> anyhow::Result<()> {
        let signed_attestation = SignedAttestation {
            message: Attestation {
                validator_id: 0,
                data: AttestationData {
                    slot: 0,
                    head: Checkpoint::default(),
                    target: Checkpoint::default(),
                    source: Checkpoint::default(),
                },
            },
            signature: Signature {
                inner: FixedBytes::default(),
            },
        };

        let encode = signed_attestation.as_ssz_bytes();
        let decoded = SignedAttestation::from_ssz_bytes(&encode);
        assert_eq!(
            hex::encode(encode),
            ""
        );
        assert_eq!(decoded, Ok(signed_attestation));

        Ok(())
    }
}
